import React,{Component} from 'react';
import Cardlist from './Cardlist';
import Searchbox from './Searchbox';
import {robots} from './robots';
 

class Search extends Component{

  constructor(){
    super()
    this.state={
    	robots: [],
    	depart:'',
      date_depart:'',
      date_arrivee:''
    }

  }

  componentDidMount(){
  this.setState({robots:robots})
  	
  }
  
onSearchchangeDepart=(event) =>{
	this.setState({depart:event.target.value})
}
onSearchchangeDd=(event) =>{
  this.setState({searchfield:event.target.value})
}
onSearchchangeDa=(event) =>{
  this.setState({searchfield:event.target.value})
}


	render(){
		const filterrobots=this.state.robots.filter(robot=>{
		return robot.depart.toLowerCase().includes(this.state.depart.toLowerCase())
	})
		console.log(filterrobots);
  return(
  	 <div className='tc'>
  	   
  	   <Searchbox searchchange={this.onSearchchangeDepart}/>
     
   <Cardlist robots={filterrobots}/>
     
     </div>
  	);
  }
}
export default Search;
