import React from 'react';
import './Bars.css';





class Bars extends Component{


constructor(){
  super();
  this.state={
    divstyle : {}
    
  }
}

openNav = () =>{
      
          this.setState({
           divstyle : {
              width: '350px', marginTop:'60px'}
           })
   }

closeNav = () =>{
         
          this.setState({
           divstyle : {
              width: '0'}
           })
   }
   render(){
	return(
        <div>
            <div class="navbar navbar1">
                <div class="navbar_left">
                    
                    <ul  class="menu">
                        <li class="li_img"><img src="./2121.jpg" alt="PDP" width="100%" class="img_pers" /></li>
                        <li>
                            <div class="dropdown">
                              <button class="dropbtn"> &or; </button>
                                  <div class="dropdown-content">
                                    <a href="#">Link 1</a>
                                    <a href="#">Link 2</a>
                                    <a href="#">Link 3</a>
                                  </div>
                            </div>
                        </li>

                    </ul>
                </div>
                <span style={{fontSize:'30px',cursor:'pointer',color: '#505978'}} onclick={this.openNav()} >&#9776;</span>
            </div>
        


            <div id="mySidenav" class="sidenav" style={this.state.divstyle}>
                <a href='#' class="closebtn" onclick={this.closeNav()}>&times;</a>
                <a href='#'>Home</a>
                <a href='#'>Creer un covoiturage</a>
                <a href='#'>Mes Messages</a>
                <a href='#'>Contact</a>
                <div class="navbar_right">
                    <ul>
                        <li><a href='#'>Sign Out</a> </li>
                    </ul>

                </div>
            </div>
            </div>
		);
}

}

export default Bars;
